function createMacrosParams(macros) {
	const macrosParams = {};

	if (macros.Fat.operator === "<" && macros.Fat.amount)
		macrosParams["maxFat"] = macros.Fat.amount;
	if (macros.Fat.operator === ">" && macros.Fat.amount)
		macrosParams["minFat"] = macros.Fat.amount;

	if (macros.Protein.operator === "<" && macros.Protein.amount)
		macrosParams["maxProtein"] = macros.Protein.amount;
	if (macros.Protein.operator === ">" && macros.Protein.amount)
		macrosParams["minProtein"] = macros.Protein.amount;

	if (macros.Carbohydrates.operator === "<" && macros.Carbohydrates.amount)
		macrosParams["maxCarbs"] = macros.Carbohydrates.amount;
	if (macros.Carbohydrates.operator === ">" && macros.Carbohydrates.amount)
		macrosParams["minCarbs"] = macros.Carbohydrates.amount;

	return macrosParams;
}

export default createMacrosParams;
