import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { Button } from "@material-ui/core";
import MacroInputs from "./MacroInputs";
import DietInputs from "./DietInputs";

const ALL_DIETS = [
	"gluten free",
	"ketogenic",
	"vegetarian",
	"lacto vegetarian",
	"ovo vegetarian",
	"lacto ovo vegetarian",
	"vegan",
	"pescetarian",
	"paleo",
	"primal",
	"whole30",
];

const ALL_MACROS = ["Fat", "Protein", "Carbohydrates"];

const useStyles = makeStyles((theme) => ({
	root: {
		display: "flex",
		backgroundImage: "linear-gradient(#c8e6c9, #fff)",
		flexWrap: "wrap",
		margin: "0 0 10px 0",
		padding: "20px",
		justifyContent: "left",
		boxShadow: "2px 2px 3px lightgray",
		maxHeight: "40vh",
	},
	button: {
		float: "right",
	},
}));

const FilterPanel = () => {
	const classes = useStyles();

	const INITIAL_FORM_DATA = {
		diets: [],
		macros: {
			Fat: {
				operator: null,
				amount: null,
			},
			Protein: {
				operator: null,
				amount: null,
			},
			Carbohydrates: {
				operator: null,
				amount: null,
			},
		},
	};

	const [formData, setFormData] = useState(INITIAL_FORM_DATA);

	const handleChange = (evt) => {
		let { name, value } = evt.target;
		if (name.startsWith("operator")) {
			name = name.slice(9);
		} else {
			name = name.slice(8);
		}

		setFormData((formData) => {
			if (ALL_MACROS.includes(name)) {
				return {
					...formData,
					macros: {
						...formData.macros,
						[name]: value,
					},
				};
			} else {
				return {
					...formData,
					diets: [...formData.diets, name],
				};
			}
		});
	};

	const handleSubmit = (evt) => {
		evt.preventDefault();
		console.log("FORM SUBMITTED");
	};

	return (
		<form className={classes.root} onSubmit={handleSubmit}>
			<DietInputs
				allDiets={ALL_DIETS}
				handleChange={handleChange}
				setFormData={setFormData}
			/>
			<MacroInputs
				allMacros={ALL_MACROS}
				handleChange={handleChange}
				setFormData={setFormData}
			/>
			<Button type="submit" className={classes.button}>
				Apply
			</Button>
		</form>
	);
};

export default FilterPanel;
