import React from "react";
import { Link } from "react-router-dom";
import { GridListTile, GridListTileBar, Badge } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { unbookmarkRecipe } from "../actionCreators/bookmarkActionCreators";

const useStyles = makeStyles((theme) => ({
	gridTile: {
		width: "100%",
	},
	badge: {
		padding: "0 !important",
	},
	img: {
		height: "100%",
	},
}));

const BookmarkTile = ({ user, recipe, setRemovingBookmark }) => {
	const classes = useStyles();

	return (
		<Badge
			badgeContent="X"
			color="secondary"
			className={classes.badge}
			onClick={() => {
				unbookmarkRecipe(recipe.id);
				setRemovingBookmark(true);
			}}
		>
			<GridListTile
				key={recipe.id}
				name={recipe.id}
				cols={1}
				className={classes.gridTile}
			>
				<Link to={`/recipes/${recipe.id}`} style={{ overflow: "hidden" }}>
					<img src={recipe.image} alt={recipe.title} className={classes.img} />
					<GridListTileBar title={recipe.title} />
				</Link>
			</GridListTile>
		</Badge>
	);
};

export default BookmarkTile;
