import axios from "axios";
import { LOGIN, LOGOUT } from "../components/actionTypes";

const API_URL = "http://localhost:5000";

// adds user to database as well as redux state
// by dispatching action created by registered()
export const register = (data) => {
	return async (dispatch) => {
		try {
			await axios.post(`${API_URL}/auth/register`, data);
			const userResp = await axios.get(`${API_URL}/users/${data.username}`);
			dispatch(registered(userResp.data));
		} catch (e) {
			console.error(e);
		}
	};
};

// returns action to be dispatched containing information on the
// newly registered user
const registered = (user) => {
	const {
		username,
		email,
		first_name,
		last_name,
		bookmarks,
		eatenMeals,
		weight,
		weight_goal,
		calorie_goal,
	} = user;
	return {
		type: LOGIN,
		payload: {
			user: {
				username,
				email,
				first_name,
				last_name,
				bookmarks,
				eatenMeals,
				weight,
				weight_goal,
				calorie_goal,
			},
		},
	};
};

// verifies that username & password from data are valid
// if valid, adds the user to redux state by dispatching action created by registered()
// in invalid, throws error
export const login = (data) => {
	return async (dispatch) => {
		try {
			const user = await axios.post(`${API_URL}/auth/login`, data);
			dispatch(loggedIn(user.data));
		} catch (e) {
			console.error(e);
		}
	};
};

// returns action to be dispatched containing information on the
// logged in user
const loggedIn = (user) => {
	const {
		username,
		email,
		first_name,
		last_name,
		bookmarks,
		eatenMeals,
		weight,
		weight_goal,
		calorie_goal,
	} = user;
	return {
		type: LOGIN,
		payload: {
			user: {
				username,
				email,
				first_name,
				last_name,
				bookmarks,
				eatenMeals,
				weight,
				weight_goal,
				calorie_goal,
			},
		},
	};
};

// returns action to be dispatched that ends up removing
// the user's information from redux state
export const logout = () => {
	return {
		type: LOGOUT,
	};
};

// verifies that username & password from data are valid
// if valid, adds the user to redux state by dispatching action created by registered()
// in invalid, throws error
export const editProfile = (username, data) => {
	return async (dispatch) => {
		try {
			const editResp = await axios.patch(`${API_URL}/users/${username}`, data);
			dispatch(edittedProfile(editResp.data));
		} catch (e) {
			console.error(e);
		}
	};
};

// returns action to be dispatched containing information on the
// logged in user
const edittedProfile = (user) => {
	return {
		type: "EDIT_PROFILE",
		payload: { user },
	};
};
