import React, { useEffect } from "react";
import { GridList, GridListTile, GridListTileBar } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { loadFeed } from "./actionCreators";
import { useDispatch, useSelector, shallowEqual } from "react-redux";

const useStyles = makeStyles(() => ({
	root: {
		display: "flex",
		flexWrap: "wrap",
		justifyContent: "space-around",
		overflow: "hidden",
	},
	gridList: {
		width: "75%",
	},
}));

const HomePage = () => {
	const classes = useStyles();
	const dispatch = useDispatch();

	const feed = useSelector((state) => state.feed, shallowEqual);
	const page = useSelector((state) => state.page, shallowEqual);
	console.log(feed);
	useEffect(() => {
		dispatch(loadFeed(page));
	}, []);

	return (
		<>
			<div className={classes.root}>
				<GridList className={classes.gridList} cols={3}>
					{/* <GridListTile cols={3} style={{ height: "auto" }}>
						<ListSubheader component="div">Recipes!</ListSubheader>
					</GridListTile> */}
					{feed.map((recipe) => (
						<GridListTile key={recipe.id} cols={1}>
							<img src={recipe.image} />
							<GridListTileBar
								title={recipe.title}
								// subtitle={<span>by: {tile.author}</span>}
								// actionIcon={
								// 	<IconButton
								// 		aria-label={`info about ${recipe.name}`}
								// 		className={classes.icon}
								// 	>
								// 		<InfoIcon />
								// 	</IconButton>
								// }
							/>
						</GridListTile>
					))}
				</GridList>
			</div>
		</>
	);
};

export default HomePage;
