import { makeStyles } from "@material-ui/core/styles";

export const useStyles = makeStyles(() => ({
	root: {
		width: "98%",
		display: "flex",
		flexDirection: "row",
		justifyContent: "center",
	},
	gridContainer: {
		height: "90%",
		width: "90%",
		boxShadow: "2px 2px 8px rgba(0,0,0,0.5)",
	},
	gridItem: { margin: "20px" },
}));
