import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import axios from "axios";
import RecipeGrid from "./RecipeGrid";
import SideNav from "./SideNav";
import { Typography } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";

const BookmarksPage = () => {
	const user = useSelector((state) => state.user);
	console.log(user);
	const bookmarks = useSelector((state) => state.user.bookmarks);
	console.log(bookmarks);

	const [fullBookmarks, setFullBookmarks] = useState([]);

	useEffect(() => {
		const loadBookmarks = async () => {
			const resp = await axios.get(
				`https://api.spoonacular.com/recipes/informationBulk`,
				{
					params: {
						ids: bookmarks.join(","),
						apiKey: "73baf9bb95a14f5fb4d71e2f12ab8479",
					},
				}
			);
			setFullBookmarks(resp.data);
		};
		loadBookmarks();
	}, [bookmarks]);

	const removeBookmark = (id) => {
		setFullBookmarks(fullBookmarks.filter((bookmark) => bookmark.id !== id));
	};

	const useStyles = makeStyles(() => ({
		root: {
			display: "flex",
			// flexWrap: "wrap",
			justifyContent: "space-around",
			// overflow: "hidden",
		},
		// mainContent: {
		// 	// float: "right",
		// 	justifyContent: "center",
		// },
	}));

	return (
		<>
			<div>
				{console.log(fullBookmarks)}
				<Typography variant="h2">Bookmarks</Typography>
				<SideNav />
				<RecipeGrid
					feed={fullBookmarks}
					areBookmarks={true}
					removeBookmark={removeBookmark}
				/>
			</div>
		</>
	);
};

export default BookmarksPage;
