const INITIAL_STATE = {
	feed: [],
	page: 1,
	countPerPage: 30,
	totalResults: null,
	user: undefined,
	currentRecipe: null,
};

const rootReducer = (state = INITIAL_STATE, action) => {
	switch (action.type) {
		case "LOAD_FEED": {
			const { recipes, totalResults } = action.payload;
			return { ...state, feed: recipes, totalResults };
		}
		case "CHANGE_PAGE": {
			return { ...state, page: action.payload };
		}
		default: {
			return state;
		}
	}
};

export default rootReducer;
