import React from "react";
import Checkbox from "@material-ui/core/Checkbox";
import { makeStyles } from "@material-ui/core/styles";
import Drawer from "@material-ui/core/Drawer";
import CssBaseline from "@material-ui/core/CssBaseline";
import Toolbar from "@material-ui/core/Toolbar";
import List from "@material-ui/core/List";
import Divider from "@material-ui/core/Divider";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import InboxIcon from "@material-ui/icons/MoveToInbox";
import MailIcon from "@material-ui/icons/Mail";
import { Typography, TextField } from "@material-ui/core";

const ALL_DIETS = [
	"gluten free",
	"ketogenic",
	"vegetarian",
	"lacto vegetarian",
	"ovo vegetarian",
	"lacto ovo vegetarian",
	"vegan",
	"pescetarian",
	"paleo",
	"primal",
	"whole30",
];

const ALL_MACROS = ["Fat", "Protein", "Carbohydrates"];

const useStyles = makeStyles((theme) => ({
	root: {
		display: "flex",
		flexWrap: "wrap",
		margin: "0 0 10px 0",
		justifyContent: "center",
		backgroundColor: "#fff",
		boxShadow: "2px 2px 3px lightgray",
		maxHeight: "40vh",
	},
	list: {
		columnCount: 2,
	},
	listTitle: {
		width: "100%",
	},
	checkbox: {
		padding: 0,
	},
	textField: {
		padding: "5px 0",
	},
}));

const FilterPanel = () => {
	const classes = useStyles();

	const [checked, setChecked] = React.useState([0]);

	const handleToggle = (value) => () => {
		const currentIndex = checked.indexOf(value);
		const newChecked = [...checked];

		if (currentIndex === -1) {
			newChecked.push(value);
		} else {
			newChecked.splice(currentIndex, 1);
		}

		setChecked(newChecked);
	};

	return (
		<div className={classes.root}>
			<div>
				<div>
					<Typography>Diets</Typography>
					<List className={classes.list}>
						{ALL_DIETS.map((diet, idx) => {
							const labelId = `checkbox-list-label-${idx}`;

							return (
								<ListItem
									key={diet}
									role={undefined}
									dense
									// button
									onClick={handleToggle(diet)}
								>
									<ListItemIcon>
										<Checkbox
											edge="start"
											checked={checked.indexOf(diet) !== -1}
											tabIndex={-1}
											disableRipple
											inputProps={{ "aria-labelledby": labelId }}
											className={classes.checkbox}
										/>
									</ListItemIcon>
									<ListItemText id={labelId} primary={diet.toUpperCase()} />
								</ListItem>
							);
						})}
					</List>
				</div>
				<div>
					<Typography>Macros</Typography>

					<List>
						{ALL_MACROS.map((macro, idx) => {
							const labelId = `checkbox-list-label-${idx}`;
							return (
								<ListItem
									key={macro}
									role={undefined}
									dense
									onClick={handleToggle(macro)}
									className={classes.textField}
								>
									<TextField size="small" label={macro} variant="outlined" />
								</ListItem>
							);
						})}
					</List>
				</div>
			</div>
		</div>
	);
};

export default FilterPanel;
