import React, { useEffect } from "react";
import { useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { loadRecipe } from "../actionCreators/recipeActionCreators";
import {
	Typography,
	Grid,
	Button,
	List,
	ListItem,
	Divider,
	ListItemText,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import PieChart from "./PieChart";
import NutrientList from "./NutrientList";

const useStyles = makeStyles(() => ({
	root: {
		margin: "0 75px",
	},
	grid: {
		margin: "20px 0",
	},
	image: {
		width: "100%",
	},
	main: {
		padding: "0 36px 0 0 !important",
	},
	infoPanel: {
		// border: "3px solid #378E3C",
		// borderRadius: "5px",
		padding: "0 !important",
	},
}));

const Recipe = () => {
	const classes = useStyles();
	const { recipeId } = useParams();
	const dispatch = useDispatch();
	const currentRecipe = useSelector((state) => state.currentRecipe);
	console.log(currentRecipe);
	const { recipe, instructions } = currentRecipe;
	const { nutrients, caloricBreakdown } = recipe.nutrition;
	const calories = nutrients[0].amount;

	const macronutrients = {
		fat: {
			name: "Fat",
			amount: nutrients[1].amount,
			subNutrients: {
				saturatedFat: {
					name: "Saturated Fat",
					amount: nutrients[2].amount,
				},
			},
		},
		carbohydrates: {
			name: "Carbohydrates",
			amount: nutrients[3].amount,
			subNutrients: {
				fiber: {
					name: "Fiber",
					amount: nutrients[13].amount,
				},
				netCarbohydrates: {
					name: "Net Carbohydrates",
					amount: nutrients[4].amount,
				},
				sugar: {
					name: "Sugar",
					amount: nutrients[5].amount,
				},
			},
		},
		protein: {
			name: "Protein",
			amount: nutrients[8].amount,
			subNutrients: {},
		},
	};

	useEffect(() => {
		const getRecipe = async () => {
			console.log("1. INITIAL RENDER");
			dispatch(loadRecipe(recipeId));
		};
		getRecipe();
	}, []);

	return (
		<div className={classes.root}>
			{currentRecipe ? (
				<>
					<Typography variant="h3">{recipe.title}</Typography>
					<Grid container spacing={3} className={classes.grid}>
						<Grid item xs={12} md={8} className={classes.main}>
							<img src={recipe.image} className={classes.image} />
							<Button>I ate this</Button>
							<Button>Bookmark</Button>
							<h2>Steps: </h2>
							<ol>
								{instructions.steps.map((step) => (
									<li key={step.number}>{step.step}</li>
								))}
							</ol>
						</Grid>
						<Grid item xs={12} md={4} className={classes.infoPanel}>
							<PieChart
								title="Caloric Breakdown"
								caloricBreakdown={caloricBreakdown}
							/>
							<NutrientList
								title="Macronutrients"
								data={macronutrients}
							></NutrientList>
						</Grid>
					</Grid>
				</>
			) : null}
		</div>
	);
};

export default Recipe;
