import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import axios from "axios";
import RecipeGrid from "./RecipeGrid";
import SideNav from "./SideNav";
import { Typography } from "@material-ui/core";

const BookmarksPage = ({ bookmarkIds }) => {
	const user = useSelector((state) => state.user);
	console.log(user);

	const [bookmarks, setBookmarks] = useState([]);
	const [removingBookmark, setRemovingBookmark] = useState(false);

	useEffect(() => {
		if (removingBookmark) {
			const loadBookmarks = async () => {
				const resp = await axios.get(
					`https://api.spoonacular.com/recipes/informationBulk`,
					{
						params: {
							ids: bookmarkIds.join(","),
							apiKey: "73baf9bb95a14f5fb4d71e2f12ab8479",
						},
					}
				);
				setBookmarks(resp.data);
			};
			loadBookmarks();
			setRemovingBookmark(false);
		}
	}, [removingBookmark]);

	return (
		<div>
			<Typography variant="h2">Bookmarks</Typography>
			<SideNav />
			<RecipeGrid
				feed={bookmarks}
				areBookmarks={true}
				setRemovingBookmark={setRemovingBookmark}
			/>
		</div>
	);
};

export default BookmarksPage;
