import React, { useEffect, useState } from "react";
import { Pagination } from "@material-ui/lab";
import { useStyles } from "./styles/HomePageStyles";
import {
	filterFeed,
	loadFeed,
} from "../../actionCreators/recipeActionCreators";
import { useDispatch, useSelector, shallowEqual } from "react-redux";
import RecipeGrid from "../recipes/RecipeGrid";
import FilterPanel from "./FilterPanel";
import useWindowDimensions from "../../customHooks/getWindowDimensions";
import getFiltered from "./getFiltered";

const HomePage = () => {
	const classes = useStyles();
	const dispatch = useDispatch();

	const user = useSelector((state) => state.user, shallowEqual);
	const feed = useSelector((state) => state.feed, shallowEqual);
	const page = useSelector((state) => state.page, shallowEqual);
	const totalResults = useSelector((state) => state.totalResults, shallowEqual);
	const countPerPage = useSelector((state) => state.countPerPage, shallowEqual);

	const { height, width } = useWindowDimensions();

	const INITIAL_FORM_DATA = {
		diets: [],
		macros: {
			Fat: {
				operator: null,
				amount: null,
			},
			Protein: {
				operator: null,
				amount: null,
			},
			Carbohydrates: {
				operator: null,
				amount: null,
			},
		},
	};

	const [filterData, setFilterData] = useState(INITIAL_FORM_DATA);

	useEffect(() => {
		const filtered = useFiltered();
		filtered
			? dispatch(filterFeed(filterData, page))
			: dispatch(loadFeed(page));
	}, [page, dispatch]);

	const handleChange = (event, value) => {
		dispatch(loadFeed(value, user.api_hash));
	};

	return (
		<>
			<div className={classes.root}>
				<div style={{ width: `${width - 240}px`, height: `${height}px` }}>
					<FilterPanel filterData={filterData} setFilterData={setFilterData} />
					<Pagination
						count={Math.ceil(totalResults / countPerPage) + 1}
						defaultPage={1}
						siblingCount={0}
						page={page}
						onChange={handleChange}
						className={classes.pagination}
					/>
					<RecipeGrid feed={feed} />
					<Pagination
						count={Math.ceil(totalResults / countPerPage) + 1}
						defaultPage={1}
						siblingCount={0}
						page={page}
						onChange={handleChange}
						className={classes.pagination}
					/>
				</div>
			</div>
		</>
	);
};

export default HomePage;
