import React, { useEffect } from "react";
import {
	TextField,
	GridList,
	GridListTile,
	GridListTileBar,
	ListSubheader,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { loadFeed } from "./actionCreators";
import { useDispatch, useSelector, shallowEqual } from "react-redux";

const useStyles = makeStyles(() => ({
	root: {
		display: "flex",
		flexWrap: "wrap",
		justifyContent: "space-around",
		overflow: "hidden",
		// height: "100%",
	},
	gridList: {
		width: "100%",
		// height: "100%",
		backgroundColor: "gray",
	},
	form: {
		margin: "0 0 50px 0",
	},
}));

const HomePage = () => {
	const classes = useStyles();
	const dispatch = useDispatch();

	const feed = useSelector((state) => state.feed, shallowEqual);
	const page = useSelector((state) => state.page, shallowEqual);
	console.log(feed);
	useEffect(() => {
		dispatch(loadFeed(page));
	}, []);

	return (
		<>
			<form className={classes.form}>
				<TextField placeholder="Search recipes" variant="outlined" />
			</form>
			<div className={classes.root}>
				<GridList className={classes.gridList} cols={3}>
					{/* <GridListTile cols={3} style={{ height: "auto" }}>
						<ListSubheader component="div">Recipes!</ListSubheader>
					</GridListTile> */}
					{feed.map((recipe) => (
						<GridListTile key={recipe.id} cols={1}>
							<img src={recipe.image} />
							<GridListTileBar
								title={recipe.title}
								// subtitle={<span>by: {tile.author}</span>}
								// actionIcon={
								// 	<IconButton
								// 		aria-label={`info about ${recipe.name}`}
								// 		className={classes.icon}
								// 	>
								// 		<InfoIcon />
								// 	</IconButton>
								// }
							/>
						</GridListTile>
					))}
				</GridList>
			</div>
		</>
	);
};

export default HomePage;
