import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import axios from "axios";
import RecipeGrid from "./RecipeGrid";
import SideNav from "./SideNav";
import { Typography } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { useWindowDimensions } from "../customHooks/getWindowDimensions";

const useStyles = makeStyles(() => ({
	root: {
		display: "flex",
		// flexWrap: "wrap",
		justifyContent: "space-around",
		// overflow: "hidden",
	},
	// mainContent: {
	// 	// float: "right",
	// 	justifyContent: "center",
	// },
}));

const BookmarksPage = () => {
	const classes = useStyles();
	const user = useSelector((state) => state.user);
	console.log(user);
	const bookmarks = useSelector((state) => state.user.bookmarks);
	console.log(bookmarks);

	const [fullBookmarks, setFullBookmarks] = useState([]);

	const { height, width } = useWindowDimensions();

	useEffect(() => {
		const loadBookmarks = async () => {
			const resp = await axios.get(
				`https://api.spoonacular.com/recipes/informationBulk`,
				{
					params: {
						ids: bookmarks.join(","),
						apiKey: "73baf9bb95a14f5fb4d71e2f12ab8479",
					},
				}
			);
			setFullBookmarks(resp.data);
		};
		loadBookmarks();
	}, [bookmarks]);

	const removeBookmark = (id) => {
		setFullBookmarks(fullBookmarks.filter((bookmark) => bookmark.id !== id));
	};

	return (
		<>
			<div className={classes.root}>
				{console.log(fullBookmarks)}
				<div
					className={classes.mainContent}
					style={{ width: `${width - 240}px`, height: `${height}px` }}
				>
					<Typography variant="h2">Bookmarks</Typography>
					<SideNav />
					<RecipeGrid
						feed={fullBookmarks}
						areBookmarks={true}
						removeBookmark={removeBookmark}
					/>
				</div>
			</div>
		</>
	);
};

export default BookmarksPage;
