import React from "react";
import {
	List,
	ListItem,
	ListItemAvatar,
	ListItemText,
	Avatar,
	Divider,
} from "@material-ui/core";

const RecipeSteps = ({ steps }) => {
	return (
		<List>
			{steps.map((step) => (
				<>
					<ListItem key={step.number} alignItems="flex-start">
						<ListItemAvatar>
							<Avatar>{step.number}</Avatar>
						</ListItemAvatar>
						<ListItemText>{step.step}</ListItemText>
					</ListItem>
					<Divider />
				</>
			))}
		</List>
	);
};

export default RecipeSteps;
