import React, { useState } from "react";
import {
	Grid,
	Typography,
	TextField,
	FormGroup,
	Button,
	Chip,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles(() => ({
	root: {
		display: "flex",
		flexDirection: "column",
		justifyContent: "center",
		alignItems: "center",
		height: "100%",
	},
	heading: {
		display: "block",
	},
	textField: {
		borderTopRightRadius: "0px",
		borderBottomLeftRadius: "0px",
	},
	ingredients: {
		boxShadow: "-2px -2px -3px lightgray",
		display: "flex",
		flexDirection: "column",
		justifyContent: "center",
		alignItems: "center",
		height: "100%",
	},
}));

const WelcomePage = () => {
	const classes = useStyles();

	const [formData, setFormData] = useState("");
	const [ingredients, setIngredients] = useState([]);

	const handleChange = (evt) => {
		setFormData(evt.target.value);
	};

	const addIngredient = (evt) => {
		evt.preventDefault();
		setIngredients((ingredients) => [...ingredients, formData]);
		setFormData("");
	};

	const deleteIngredient = (evt) => {
		evt.preventDefault();
		console.log(evt.target.label);
	};

	const handleSubmit = (evt) => {
		evt.preventDefault();
		console.log("SUBMITTED!");
	};

	return (
		<Grid container cols={2}>
			<Grid item cols={1} md={9}>
				<div className={classes.root}>
					<Typography className={classes.heading}>
						What's in your kitchen?
					</Typography>
					<form onSubmit={handleSubmit}>
						<FormGroup row>
							<TextField
								variant="outlined"
								size="small"
								className={classes.textField}
								onChange={handleChange}
							/>
							<Button variant="outlined" onClick={addIngredient}>
								Add Item
							</Button>
						</FormGroup>

						<Button type="submit">Submit</Button>
					</form>
				</div>
			</Grid>
			<Grid item cols={1} md={3} class={classes.ingredients}>
				{ingredients.map((ingredient) => (
					<Chip label={ingredient} onDelete={handleDelete} />
				))}
			</Grid>
		</Grid>
	);
};

export default WelcomePage;
