import React from "react";
import {
	Grid,
	Typography,
	TextField,
	FormGroup,
	Button,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles(() => ({
	leftGridItem: {
		height: "100%",
	},
	main: {
		display: "flex",
		flexDirection: "column",
		justifyContent: "center",
		alignItems: "center",
		height: "50%",
		width: "75%",
		borderRadius: "5px",
		backgroundColor: "rgba(255,255,255,0.5)",
		boxShadow: "2px 2px 10px black",
	},
	heading: {
		display: "block",
		margin: "20px 0",
	},
	textField: {
		borderTopRightRadius: "0px",
		borderBottomRightRadius: "0px",
		backgroundColor: "#fff",
		width: "100%",
	},
	addItemBtn: {
		backgroundColor: "#4caf50",
		color: "#fff",
		"&:hover": {
			backgroundColor: "#81c784",
		},
		borderTopLeftRadius: "0px",
		borderBottomLeftRadius: "0px",
	},
}));

const IngredientInput = ({
	ingredients,
	handleChange,
	formData,
	addIngredient,
}) => {
	const classes = useStyles();

	return (
		<Grid item cols={1} md={8} className={classes.leftGridItem}>
			<form
				style={{
					width: "100%",
					height: "100%",
					display: "flex",
					flexDirection: "column",
					justifyContent: "center",
					alignItems: "center",
				}}
			>
				<div className={classes.main}>
					<Typography variant="h4" className={classes.heading}>
						What's in your kitchen?
					</Typography>
					<FormGroup row>
						<TextField
							variant="outlined"
							size="small"
							// className={classes.textField}
							onChange={handleChange}
							value={formData}
							error={ingredients.includes(formData)}
							helperText={
								ingredients.includes(formData)
									? `${formData} has already been selected.`
									: null
							}
							InputProps={{ className: classes.textField }}
						/>
						<Button onClick={addIngredient} className={classes.addItemBtn}>
							Add Item
						</Button>
					</FormGroup>
				</div>
			</form>
		</Grid>
	);
};

export default IngredientInput;
