import { makeStyles } from "@material-ui/core/styles";

export const useStyles = makeStyles((theme) => ({
	root: {
		display: "flex",
		flexWrap: "wrap",
		justifyContent: "space-around",
		overflow: "hidden",
	},
	gridList: {
		width: "max-content",
		justifyContent: "center",
	},
	icon: {
		color: "rgba(255, 255, 255, 0.54)",
	},
	img: {
		width: "100%",
	},
	missingIngredients0: {
		width: "20%",
		top: 0,
		right: 0,
		backgroundColor: "#378e3c",
		borderBottomRightRadius: "15px",
		width: "30%",
		boxShadow: "2px 2px 8px rgba(0,0,0,0.5)",
	},
	missingIngredients1: {
		width: "20%",
		top: 0,
		right: 0,
		backgroundColor: "#cddc39",
		borderBottomRightRadius: "15px",
		width: "30%",
		boxShadow: "2px 2px 8px rgba(0,0,0,0.5)",
		"& > div": {
			// color: "#000",
			textShadow: "2px 2px 2px black",
		},
	},
	missingIngredients2: {
		width: "20%",
		top: 0,
		right: 0,
		backgroundColor: "#ffeb3b",
		// color: "#000 !important",
		borderBottomRightRadius: "15px",
		width: "30%",
		boxShadow: "2px 2px 8px rgba(0,0,0,0.5)",
		"& > div": {
			color: "#000",
		},
	},
	missingIngredients3: {
		width: "20%",
		top: 0,
		right: 0,
		backgroundColor: "#ff9800",
		borderBottomRightRadius: "15px",
		width: "30%",
		boxShadow: "2px 2px 8px rgba(0,0,0,0.5)",
	},
	missingIngredients4: {
		width: "20%",
		top: 0,
		right: 0,
		backgroundColor: "#ff3d00",
		borderBottomRightRadius: "15px",
		width: "30%",
		boxShadow: "2px 2px 8px rgba(0,0,0,0.5)",
	},
