import React from "react";
import { useSelector } from "react-redux";
import {
	GridList,
	GridListTile,
	ListSubheader,
	GridListTileBar,
	IconButton,
} from "@material-ui/core";
import InfoIcon from "@material-ui/icons/Info";
import { makeStyles } from "@material-ui/core/styles";
import useWindowDimensions from "../../customHooks/getWindowDimensions";
import RecipeTile from "./RecipeTile";
import BookmarkTile from "./BookmarkTile";

const useStyles = makeStyles((theme) => ({
	root: {
		display: "flex",
		flexWrap: "wrap",
		justifyContent: "space-around",
		overflow: "hidden",
	},
	gridList: {
		width: "max-content",
		// height: "100%",
		// padding: "20px",
	},
}));

const RecipeGrid = ({ feed, areBookmarks, removeBookmark }) => {
	const classes = useStyles();

	const { width } = useWindowDimensions();

	const user = useSelector((state) => state.user);

	return (
		<div className={classes.root}>
			<GridList
				cellHeight={180}
				spacing={3}
				cols={3}
				className={classes.gridList}
			>
				{/* <GridListTile key="Subheader" cols={2} style={{ height: "auto" }}>
					<ListSubheader component="div">December</ListSubheader>
				</GridListTile> */}
				{feed.map((recipe) => (
					<GridListTile key={recipe.image}>
						<img src={recipe.image} alt={recipe.title} />
						<GridListTileBar
							title={recipe.title}
							subtitle={<span>by: {recipe.author}</span>}
							actionIcon={
								<IconButton
									aria-label={`info about ${recipe.title}`}
									// className={classes.icon}
								>
									<InfoIcon />
								</IconButton>
							}
						/>
					</GridListTile>
					// <RecipeTile cols={1} recipe={recipe} />
				))}
			</GridList>
		</div>
		// <div className={classes.root}>
		// 	<GridList
		// 		cols={Math.round(width / 300)}
		// 		spacing={3}
		// 		cellHeight={180}
		// 		className={classes.gridList}
		// 		justify="center"
		// 	>
		// 		{feed.map((recipe) =>
		// 			areBookmarks ? (
		// 				<BookmarkTile
		// 					user={user}
		// 					recipe={recipe}
		// 					removeBookmark={removeBookmark}
		// 					key={recipe.id}
		// 				/>
		// 			) : (
		// 				<RecipeTile recipe={recipe} key={recipe.id} />
		// 			)
		// 		)}
		// 	</GridList>
		// </div>
	);
};

export default RecipeGrid;
