import React from "react";
import Checkbox from "@material-ui/core/Checkbox";
import { makeStyles } from "@material-ui/core/styles";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import {
	Typography,
	TextField,
	FormControl,
	Grid,
	Button,
} from "@material-ui/core";
import Select from "@material-ui/core/Select";

const ALL_DIETS = [
	"gluten free",
	"ketogenic",
	"vegetarian",
	"lacto vegetarian",
	"ovo vegetarian",
	"lacto ovo vegetarian",
	"vegan",
	"pescetarian",
	"paleo",
	"primal",
	"whole30",
];

const ALL_MACROS = ["Fat", "Protein", "Carbohydrates"];

const useStyles = makeStyles((theme) => ({
	root: {
		display: "flex",
		backgroundImage: "linear-gradient(#c8e6c9, #fff)",
		flexWrap: "wrap",
		margin: "0 0 10px 0",
		padding: "20px",
		justifyContent: "left",
		boxShadow: "2px 2px 3px lightgray",
		maxHeight: "40vh",
	},
	list: {
		columnCount: 2,
	},
	listTitle: {
		width: "100%",
	},
	listItem: {
		verticalAlign: "top",
	},
	checkbox: {
		padding: 0,
	},
	textField: {
		padding: "5px 0",
		display: "flex",
		flexDirection: "row",
		justifyContent: "space-around",
	},
	selectOption: {
		margin: "5px",
	},
	button: {
		float: "right",
	},
}));

const FilterPanel = () => {
	const classes = useStyles();

	const [checked, setChecked] = React.useState([0]);

	const handleToggle = (value) => () => {
		const currentIndex = checked.indexOf(value);
		const newChecked = [...checked];

		if (currentIndex === -1) {
			newChecked.push(value);
		} else {
			newChecked.splice(currentIndex, 1);
		}

		setChecked(newChecked);
	};

	return (
		<form className={classes.root}>
			<div style={{ float: "left", width: "50%" }}>
				<Typography>Diets</Typography>
				<List className={classes.list}>
					{ALL_DIETS.map((diet, idx) => {
						const labelId = `checkbox-list-label-${idx}`;

						return (
							<ListItem
								key={diet}
								// role={undefined}
								dense
								// button
								onClick={handleToggle(diet)}
								className={classes.listItem}
							>
								<ListItemIcon>
									<Checkbox
										edge="start"
										checked={checked.indexOf(diet) !== -1}
										tabIndex={-1}
										disableRipple
										inputProps={{ "aria-labelledby": labelId }}
										className={classes.checkbox}
									/>
								</ListItemIcon>
								<ListItemText id={labelId} primary={diet.toUpperCase()} />
							</ListItem>
						);
					})}
				</List>
			</div>
			<div style={{ float: "left", width: "50%" }}>
				<Typography>Macros</Typography>

				{ALL_MACROS.map((macro, idx) => {
					// const labelId = `checkbox-list-label-${idx}`;
					return (
						<Grid
							container
							cols={2}
							spacing={2}
							alignItems="flex-end"
							key={macro}
						>
							<Grid
								item
								onClick={handleToggle(macro)}
								// className={classes.textField}
								cols={1}
								md={3}
							>
								<FormControl variant="outlined" fullWidth size="small">
									<Select size="small" label="Comparison">
										<option
											value={"<"}
											label="Less than"
											className={classes.selectOption}
										/>
										<option
											value={">"}
											label="Greater than"
											className={classes.selectOption}
										/>

										<option
											value={"==="}
											label="Equal to"
											className={classes.selectOption}
										/>
									</Select>
								</FormControl>
							</Grid>
							<Grid
								item
								// key={macro}
								onClick={handleToggle(macro)}
								// className={classes.textField}
								cols={1}
								md={3}
							>
								<TextField variant="outlined" size="small" label={macro} />
							</Grid>
							<Grid
								item
								// key={macro}
								// onClick={handleToggle(macro)}
								// className={classes.textField}
								cols={1}
								md={3}
							>
								<Typography style={{ display: "inline" }}>grams</Typography>
							</Grid>
						</Grid>
					);
				})}
			</div>
			<Button className={classes.button}>Apply</Button>
		</form>
	);
};

export default FilterPanel;
