import axios from "axios";
import { BOOKMARK_RECIPE } from "../components/actionTypes";

const API_URL = "http://localhost:5000";

export const bookmarkRecipe = (username, recipeId) => {
	return async () => {
		await axios.post(`${API_URL}/bookmarkRecipe`, {
			username,
			recipeId,
		});
	};
};
