import React, { useState, useEffect } from "react";
import axios from "axios";
import {
	Grid,
	Typography,
	TextField,
	FormGroup,
	Button,
	Chip,
	Divider,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { useDispatch, useSelector } from "react-redux";
import { useHistory } from "react-router-dom";
// import { getRecipesByIngredients } from "../../actionCreators/recipeActionCreators";
import RecipeGrid from "../recipes/RecipeGrid";
// import { animateScroll } from "react-scroll";

import IngredientInput from "./IngredientInput";

const useStyles = makeStyles(() => ({
	root: {
		backgroundImage:
			"url(https://images.unsplash.com/photo-1470549813517-2fa741d25c92?ixid=MXwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHw%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=1650&q=80)",
		backgroundSize: "cover",
	},
	submitBtn: {
		backgroundColor: "#4caf50",
		color: "#fff",
		"&:hover": {
			backgroundColor: "#81c784",
		},
		borderTopLeftRadius: 0,
		borderTopRightRadius: 0,
	},
	rightGridItem: {
		height: "100%",
		display: "flex",
		flexDirection: "column",
		justifyContent: "center",
		alignItems: "center",
	},
	ingredients: {
		boxShadow: "-2px -2px 10px black",
		backgroundColor: "#fff",
		borderRadius: "5px",
		display: "flex",
		flexDirection: "column",
		// justifyContent: "center",
		alignItems: "center",
		height: "90%",
		width: "80%",
		position: "relative",
		backgroundColor: "none",
	},
}));

const WelcomePage = () => {
	const classes = useStyles();
	const dispatch = useDispatch();
	const history = useHistory();

	const [formData, setFormData] = useState("");
	const [ingredients, setIngredients] = useState([]);
	const [formSubmitted, setFormSubmitted] = useState(false);
	const [results, setResults] = useState([]);

	const feed = useSelector((state) => state.feed);

	const handleChange = (evt) => {
		setFormData(evt.target.value);
	};

	const addIngredient = (evt) => {
		evt.preventDefault();
		setIngredients((ingredients) => [...ingredients, formData]);
	};

	const deleteIngredient = (ingredient) => {
		setIngredients((ingredients) =>
			ingredients.filter((ing) => ing !== ingredient)
		);
	};

	const handleSubmit = (evt) => {
		evt.preventDefault();
		// dispatch(getRecipesByIngredients(ingredients));
		setFormSubmitted(true);
	};

	useEffect(() => {
		if (formSubmitted) {
			const getResults = async () => {
				try {
					const ingredientsParams = ingredients.join(",");
					const recipes = await axios.get(
						"https://api.spoonacular.com/recipes/findByIngredients",
						{
							params: {
								apiKey: "73baf9bb95a14f5fb4d71e2f12ab8479",
								offset: 0,
								number: 6900,
								ingredients: ingredientsParams,
							},
						}
					);
					setResults(recipes.data);
				} catch (e) {
					console.error(e);
				}
			};
			getResults();
		}
	}, [formSubmitted]);

	useEffect(() => {
		setFormData("");
	}, [ingredients]);

	return (
		<Grid container cols={2} className={classes.root}>
			<IngredientInput
				ingredients={ingredients}
				handleChange={handleChange}
				formData={formData}
				addIngredient={addIngredient}
			/>
			<Grid item cols={1} md={4} className={classes.rightGridItem}>
				<div className={classes.ingredients}>
					<Typography
						variant="h5"
						style={{
							padding: "10px 0",
							width: "100%",
							backgroundColor: "#fff",
							borderTopLeftRadius: "5px",
							borderTopRightRadius: "5px",
							textAlign: "center",
							height: "5%",
						}}
					>
						Selected Ingredients
					</Typography>
					<Divider style={{ width: "100%" }} />
					<div
						style={{
							height: "87%",
							width: "100%",
							backgroundColor: "rgba(255,255,255,0.5)",
						}}
					>
						<div
							style={{
								display: "flex",
								justifyContent: "center",
								flexWrap: "wrap",
								margin: "10px",
							}}
						>
							{ingredients.map((ingredient) => (
								<Chip
									key={ingredient}
									label={ingredient}
									onDelete={() => deleteIngredient(ingredient)}
									style={{ margin: "5px" }}
								/>
							))}
						</div>
					</div>
					<Button
						type="submit"
						onClick={handleSubmit}
						className={classes.submitBtn}
						style={{
							position: "absolute",
							bottom: "0",
							width: "100%",
							height: "5%",
							boxShadow: "-2px 0px 8px rgba(0,0,0,0.5)",
						}}
					>
						Submit
					</Button>
				</div>
			</Grid>
			{formSubmitted ? (
				results.length ? (
					<Grid
						item
						cols={2}
						xs={12}
						style={{ margin: "10px 0", padding: "0 10px 0 40px" }}
					>
						<RecipeGrid feed={results} ingredients={ingredients} />
					</Grid>
				) : (
					<div
						style={{
							width: "100%",
							height: "80px",
							display: "flex",
							flexDirection: "column",
							justifyContent: "center",
						}}
					>
						<Typography
							style={{
								color: "red",
								textAlign: "center",
							}}
						>
							No results found
						</Typography>
					</div>
				)
			) : null}
		</Grid>
	);
};

export default WelcomePage;
