import React, { useState } from "react";
import { Typography, TextField, Button, Grid } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import signup_facts from "../images/signup_facts.png";
import SignUpForm from "./SignUpForm";
import { useHistory } from "react-router-dom";

const useStyles = makeStyles(() => ({
	root: {
		margin: "0 auto",
		width: "100%",
		height: "100%",
		flexGrow: 1,
	},
	gridItem: {
		height: "75%",
	},
	signUp: {
		backgroundColor: "white",
		padding: "35px 50px !important",
		borderRadius: "5px",
	},
	signUpFacts: {
		width: "100%",
	},
}));

const SignUpPage = () => {
	const classes = useStyles();

	const INITIAL_FORM_DATA = {
		first_name: "",
		last_name: "",
		username: "",
		email: "",
		password: "",
	};
	const [formData, setFormData] = useState(INITIAL_FORM_DATA);

	const handleChange = (evt) => {
		const { name, value } = evt.target;
		setFormData((formData) => ({
			...formData,
			[name]: value,
		}));
	};

	const handleSubmit = (evt) => {
		evt.preventDefault();
	};

	return (
		<Grid
			container
			alignItems="center"
			cols={2}
			spacing={3}
			className={classes.root}
		>
			<Grid
				item
				cols={1}
				xs={12}
				md={6}
				className={`${classes.signUp} ${classes.gridItem}`}
			>
				<Typography variant="h4">
					<b>Sign Up</b>
				</Typography>
				<SignUpForm handleChange={handleChange} handleSubmit={handleSubmit} />
			</Grid>
			<Grid cols={1} item xs={12} md={6} className={classes.gridItem}>
				<img src={signup_facts} className={classes.signUpFacts} />
			</Grid>
		</Grid>
	);
};

export default SignUpPage;
