import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import { Grid, Typography, ButtonGroup, Button } from "@material-ui/core";
import { useStyles } from "./styles/TrackerPageStyles";
import { Doughnut, Bar } from "react-chartjs-2";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import convertDate from "../../helpers/convertDate";
import { getDailyMacros, getDateMacros } from "./helpers/dailyHelpers";
import {
	getWeeklyMacros,
	getWeekNumber,
	getWeekDates,
	getWeekMacros,
	updateWeekData,
} from "./helpers/weeklyHelpers";

const TrackerPage = () => {
	const classes = useStyles();
	const user = useSelector((state) => state.user);
	const [calendarDate, setCalendarDate] = useState(new Date());
	const [dayState, setDayState] = useState({
		date: convertDate(calendarDate),
		macros: {
			carbs: 0,
			fat: 0,
			protein: 0,
		},
	});
	const [weekState, setWeekState] = useState({
		weekNumber: null,
		dates: getWeekDates(calendarDate),
		weekData: {},
	});

	const [pieChartData, setPieChartData] = useState(getDailyMacros());
	const [barChartData, setBarChartData] = useState(
		getWeeklyMacros(weekState.weekData)
	);

	useEffect(() => {
		getDateMacros(user, "day", convertDate(calendarDate), setDayState);
		if (getWeekNumber(calendarDate) !== weekState.weekNumber) {
			setWeekState({
				weekNumber: getWeekNumber(calendarDate),
				dates: getWeekDates(calendarDate),
				weekData: getWeekMacros(getWeekDates(calendarDate)),
			});
		}
	}, [calendarDate]);

	useEffect(() => {
		setPieChartData(
			getDailyMacros(
				dayState.macros.carbs,
				dayState.macros.fat,
				dayState.macros.protein
			)
		);
	}, [dayState]);

	useEffect(() => {
		setBarChartData(getWeeklyMacros(weekState.weekData));
	}, [weekState]);

	return (
		<div style={{ width: "100%" }}>
			{console.log(weekState)}
			<Grid container cols={2} className={classes.root}>
				<Grid item cols={1} md={4} className={classes.gridItem}>
					<div className={classes.calendarDiv}>
						<Calendar
							onChange={setCalendarDate}
							value={calendarDate}
							minDetail="year"
							calendarType="US"
							className={classes.calendar}
						/>
					</div>
					<div className={classes.doughnutDiv}>
						<Typography>Daily Macros</Typography>
						<Doughnut data={pieChartData} className={classes.doughnut} />
					</div>
				</Grid>
				<Grid item cols={1} md={8} className={classes.gridItem}>
					<div className={classes.barChartDiv}>
						<Typography variant="h3">Weekly Data</Typography>
						<Bar data={barChartData} className={classes.barChart} />
					</div>
				</Grid>
			</Grid>
		</div>
	);
};

export default TrackerPage;
