const INITIAL_STATE = {
	feed: [],
	page: 1,
	user: undefined,
};

const rootReducer = (state = INITIAL_STATE, action) => {
	switch (action.type) {
		case "LOAD_FEED": {
			return { ...state, feed: action.payload };
		}
		case "CHANGE_PAGE": {
			return { ...state, page: action.payload };
		}
		default: {
			return state;
		}
	}
};

export default rootReducer;
