import { makeStyles } from "@material-ui/core/styles";

export const useStyles = makeStyles(() => ({
	root: {
		margin: "0 0 20px 0",
		backgroundColor: "#fff",
		padding: "20px",
		// boxShadow: "2px 3px 3px lightgray",
	},
	step: {
		margin: "20px 0",
	},
}));
